properties.widgets.LocalFilePlugin-batchsource:
{
  "outputs": [{
    "name": "schema",
    "widget-type": "schema",
    "label": "Schema"
  }],
  "metadata": {"spec-version": "1.5"},
  "configuration-groups": [
    {
      "label": "Basic Properties",
      "properties": [
        {
          "widget-type": "textbox",
          "name": "referenceName",
          "label": "Reference Name",
          "widget-attributes": {"placeholder": "Enter the reference name."}
        },
        {
          "name": "filePath",
          "widget-type": "text-box",
          "label": "Path",
          "placeholder": "/path/to/input"
        },
        {
          "name": "format",
          "widget-type": "select",
          "label": "Format",
          "widget-attributes": {
            "key-placeholder": "choose format",
            "values": [
              "csv",
              "tsv",
              "file"
            ]
          }
        }
      ]
    },
    {
      "label": "Advanced Properties",
      "properties": [
        {
          "name": "headers",
          "widget-type": "toggle",
          "label": "Include Headers",
          "widget-attributes": {
            "default": "false",
            "off": {
              "label": "No",
              "value": "false"
            },
            "on": {
              "label": "Yes",
              "value": "true"
            }
          }
        },
        {
          "widget-type": "radio-group",
          "name": "emptyInput",
          "label": "Allow empty values",
          "widget-attributes": {
            "layout": "inline",
            "default": "True",
            "options": [
              {
                "id": "True",
                "label": "True"
              },
              {
                "id": "False",
                "label": "False"
              }
            ]
          }
        },
        {
          "name": "delimiter",
          "widget-type": "select",
          "label": "Delimiter",
          "widget-attributes": {
            "default": ",",
            "values": [
              ",",
              ";",
              ":",
              ".",
              "|",
              " "
            ]
          }
        }
      ]
    }
  ]
}
